mapper.select:
SELECT eventno, title, contents , file1, file1saved, thumb1, size1, rdate
FROM Event
WHERE eventno = #{eventno}

mapper.update:
UPDATE Event
SET title=#{title}, contents=#{contents}
WHERE eventno = #{eventno}

UPDATE Event
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE eventno = #{eventno}
