mapper.select:
SELECT COUNT(id) as cnt
FROM member
WHERE id=#{id}

SELECT memberno, id, passwd, mname, tel, zipcode, address1, address2, mdate, grade
FROM member
WHERE memberno = #{memberno}

SELECT memberno, id, passwd, mname, tel, zipcode, address1, address2, mdate, grade
FROM member
WHERE id = #{id}

mapper.update:
UPDATE member 
SET id=#{id}, mname=#{mname}, tel=#{tel}, zipcode=#{zipcode}, address1=#{address1}, address2=#{address2}
WHERE memberno=#{memberno}


UPDATE member
SET grade = 99
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(memberno) as cnt
FROM member
WHERE id=#{id} AND passwd=#{passwd}

SELECT COUNT(memberno) as cnt
FROM member
WHERE memberno=#{memberno} AND passwd=#{passwd}

mapper.update:
UPDATE member
SET passwd=#{passwd}
WHERE memberno=#{memberno}

mapper.select:
SELECT COUNT(*) as cnt
FROM member
WHERE mname=#{name} AND tel=#{tel}

SELECT memberno, id
FROM member
WHERE tel = #{tel}

SELECT COUNT(*) as cnt
FROM Member
WHERE mname = #{name} AND id = #{id}
