mapper.select:
SELECT COUNT(*) as cnt FROM order_pay
WHERE memberno=#{memberno}
WHERE memberno=#{memberno} AND (UPPER(rname) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(raddress1) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT order_payno, memberno, rname, rtel, rzipcode, raddress1, raddress2, paytype, amount, rdate, r FROM ( SELECT order_payno, memberno, rname, rtel, rzipcode, raddress1, raddress2, paytype, amount, rdate, rownum as r FROM ( SELECT order_payno, memberno, rname, rtel, rzipcode, raddress1, raddress2, paytype, amount, rdate FROM order_pay
WHERE memberno=#{memberno}
WHERE memberno=#{memberno} AND (UPPER(rname) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(raddress1) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY order_payno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}
