mapper.select:
SELECT ProductID, categoryID, SupplierID, Origin, PName, Description, Volume, AlcoholContent, Price, ImageFile, ImageFileSaved, thumb, sizes, Pcnt, recom
FROM Products 
WHERE ProductID = #{ProductID}

SELECT COUNT(*) as cnt FROM Products
WHERE categoryID=#{categoryID}
WHERE categoryID=#{categoryID} AND (UPPER(PName) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(Description) LIKE '%' || UPPER(#{word}) || '%' )

SELECT ProductID, categoryID, SupplierID, Origin, PName, Description, Volume, AlcoholContent, Price, ImageFile, ImageFileSaved, thumb, sizes, Pcnt, recom, r FROM ( SELECT ProductID, categoryID, SupplierID, Origin, PName, Description, Volume, AlcoholContent, Price, ImageFile, ImageFileSaved, thumb, sizes, Pcnt, recom, rownum as r FROM ( SELECT ProductID, categoryID, SupplierID, Origin, PName, Description, Volume, AlcoholContent, Price, ImageFile, ImageFileSaved, thumb, sizes, Pcnt, recom FROM Products
WHERE categoryID=#{categoryID}
WHERE categoryID=#{categoryID} AND (UPPER(title) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(content) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY ProductID DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE Products
SET PName=#{PName}, Description=#{Description}
WHERE ProductID = #{ProductID}

UPDATE Products
SET ImageFile=#{ImageFile}, ImageFileSaved=#{ImageFileSaved}, thumb=#{thumb}, sizes=#{sizes}
WHERE ProductID = #{ProductID}

